const handleClick=o=>{o.preventDefault();const t=o.target.getAttribute("href");this.location.hash=t,scrollToHash(t)};function scrollToHash(o){const t=document.querySelectorAll(o)[0].offsetTop;window.scrollTo({top:t-200,behavior:"smooth"})}$(document).ready((function(){document.querySelectorAll(".about-company__navigation-item").forEach(o=>o.addEventListener("click",handleClick));const o=new URL(window.location).hash;o&&scrollToHash(o)}));