let open_btn=document.getElementsByClassName("buy-car__open"),texts=document.getElementsByClassName("buy-car__content");console.log(open_btn.length);for(let t=0;t<open_btn.length;t++)open_btn[t].addEventListener("click",(function(){texts[t].classList.contains("full-text")?(texts[t].classList.remove("full-text"),open_btn[t].classList.remove("open-btn")):(texts[t].classList.add("full-text"),open_btn[t].classList.add("open-btn"))}));